import React, { useEffect, useState } from 'react';
import api from '../api';

function TaskList({ onEdit }) {
  const [tasks, setTasks] = useState([]);

  const fetchTasks = async () => {
    const res = await api.get('/tasks');
    setTasks(res.data);
  };

  useEffect(() => { fetchTasks(); }, []);

  const toggleComplete = async (task) => {
    await api.put(`/tasks/${task.id}`, { completed: !task.completed });
    fetchTasks();
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Eliminar tarea?')) {
      await api.delete(`/tasks/${id}`);
      fetchTasks();
    }
  };

  return (
    <div style={{ margin: '2rem' }}>
      <h2>Mis Tareas</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {tasks.map(t => (
          <li key={t.id} style={{ marginBottom: '1rem', borderBottom: '1px solid #eee', paddingBottom: '0.5rem' }}>
            <strong>{t.title}</strong> — {t.priority} — {new Date(t.due_date).toLocaleDateString()} — {t.completed ? '✔️' : '❌'}
            <div>
              <button onClick={() => toggleComplete(t)}>Toggle</button>
              <button onClick={() => onEdit(t)}>Editar</button>
              <button onClick={() => handleDelete(t.id)}>Eliminar</button>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
}

export default TaskList;
