import React, { useState, useEffect } from 'react';
import api from '../api';

const defaultForm = { title: '', description: '', priority: 'baja', due_date: '' };

function TaskForm({ editing, onSaved, cancelEdit }) {
  const [form, setForm] = useState(defaultForm);

  useEffect(() => {
    if (editing) {
      setForm({
        title: editing.title,
        description: editing.description || '',
        priority: editing.priority,
        due_date: editing.due_date.slice(0, 10),
      });
    } else {
      setForm(defaultForm);
    }
  }, [editing]);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      const payload = { ...form, due_date: new Date(form.due_date) };
      if (editing) {
        await api.put(`/tasks/${editing.id}`, payload);
      } else {
        await api.post('/tasks', payload);
      }
      onSaved();
      setForm(defaultForm);
    } catch (err) {
      alert(err.response?.data?.detail || 'Error al guardar');
    }
  };

  return (
    <form onSubmit={handleSubmit} style={{ margin: '2rem', maxWidth: 400 }}>
      <h2>{editing ? 'Editar Tarea' : 'Nueva Tarea'}</h2>
      <input
        name="title"
        placeholder="Título"
        value={form.title}
        onChange={handleChange}
        required
        style={{ display: 'block', width: '100%', marginBottom: '1rem' }}
      />
      <textarea
        name="description"
        placeholder="Descripción"
        value={form.description}
        onChange={handleChange}
        style={{ display: 'block', width: '100%', marginBottom: '1rem' }}
      />
      <select
        name="priority"
        value={form.priority}
        onChange={handleChange}
        style={{ display: 'block', width: '100%', marginBottom: '1rem' }}
      >
        <option value="baja">Baja</option>
        <option value="media">Media</option>
        <option value="alta">Alta</option>
      </select>
      <input
        type="date"
        name="due_date"
        value={form.due_date}
        onChange={handleChange}
        required
        style={{ display: 'block', width: '100%', marginBottom: '1rem' }}
      />
      <button type="submit">{editing ? 'Actualizar' : 'Crear'}</button>
      {editing && <button type="button" onClick={cancelEdit}>Cancelar</button>}
    </form>
  );
}

export default TaskForm;
